drop table SIGHTS;

CREATE TABLE SIGHTS ( SIGHTS_NAME VARCHAR2(400)  PRIMARY KEY,
  CITY VARCHAR2(20) REFERENCES CITY(PROVINCE) ON DELETE CASCADE,
  SIGHTS_REGION VARCHAR2(400),
  SIGHTS_DESCRIPTION VARCHAR2(4000),
  SIGHTS_LOCATION VARCHAR2(2000),
  SIGHTS_HOMEPAGE VARCHAR2(400),
  SIGHTS_LATITUDE VARCHAR2(400),
  SIGHTS_LONGITUDE VARCHAR2(400));


--행 1
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('인사동','서울/경기','종로구','인사동은 도심 속에서 낡지만 귀중한 전통의 물건들이 교류되는 소중한 공간이다. 인사동에는 큰 대로를 중심으로 사이사이 골목들이 미로처럼 얽혀 있다. 이 미로 속에는 화랑, 전통공예점, 고미술점, 전통찻집, 전통음식점, 카페 등이 밀집되어 있다. 특히 인사동의 가게들은 저마다의 독특한 멋으로 젊은이들은 물론 중년층에게까지 매우 인기가 많다. 그 중에서도 화랑은 인사동의 맥을 이어온 중심이다. 이곳에는 100여 개의 화랑이 밀집되어 있는데 한국화에서 판화, 조각전까지 다양한 전시회를 감상할 수 있다. 대표적인 화랑으로는 민중미술의 중심역할을 했던 학고재, 재능있는 작가들의 터전이 되었던 가나화랑, 가나아트센터 등이 있다. 만약 인사동의 유명한 화랑만을 골라서 편하게 구경하고 싶다면 미술관 순환버스를 이용하면 저렴한 가격으로 유명 화랑 10여곳을 둘러볼 수 있다. 화랑과 함께 인사동 거리를 메우고 있는 것은 바로 전통 찻집과 음식점이다. 처음에는 찾기가 힘들지만, 산책하듯이 천천히 골목골목을 둘러보면 매우 재미있다. 그 중에서 대표적인 명소를 꼽는다면 전통찻집 귀천이 있다. 귀천은 고 천상병시인의 부인이 운영하는 전통찻집으로 내부는 매우 작지만 당시 시인의 책과 사진을 접할 수 있는 곳으로 지금도 가끔 유명 인사들이 다녀가는 곳이다.','서울특별시 종로구 인사동길 62 (관훈동) 일대','http://www.insainfo.or.kr ','37.5720449','126.9860059');
--행 2
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('명동거리','서울/경기','중구','명동은 거대 쇼핑도시를 연상케하는 공간이다. 일반적으로 명동 거리는 지하철 4호선 명동역에서 을지로, 롯데백화점으로 이어지는 약 1km 정도의 거리를 말한다. 이곳에는 각종 브랜드매장, 백화점, 보세가게 등이 밀집되어 있다. 유행의 메카라는 표현이 어울릴 정도로 의류, 신발, 액세서리 등의 다양한 제품을 구입할 수 있다. 남대문이나 동대문보다는 질이 좋은 브랜드가 많이 모여 있는 것이 특징이다. 우선 백화점으로는 가까이에 롯데백화점, 신세계백화점이 있으며, 명동거리에는 눈스퀘어(Noon Square), 명동밀레오레, 엠플라자(M Plaza)와 같은 쇼핑몰이 있다. 각종 브랜드숍은 중앙거리를 비롯해 사이드 골목에 밀집되어 있다.  명동에는 쇼핑과 함께 먹을거리, 즐길거리가 많다. 먹을거리로는 패밀리레스토랑, 패스트푸드점은 물론 한식, 양식, 일식으로 다양하다. 그 외에도 헤어샵, 은행, 극장 등 많은 편의시설이 있다.','서울특별시 중구 명동길 (명동1가)','http://tour.junggu.seoul.kr/tour/','37.5615682','126.9733249');
--행 3
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('남산/N서울타워','서울/경기','용산구','서울타워는 한국 최초의 타워형태의 관광명소다. 높이 236.7m를 헤아리는 타워는 해발 243m인 남산의 높이까지 계산하면 실제 높이는 480m에 이르러 가히 동양최고의 타워라고 자랑할 만하다. 1969년 12월 착공, 1975년 7월 완공됐고, 1980년 10월 15일 전망대가 일반인에게 공개되면서 서울의 명소로 면모를 드러냈다. 그동안 서울타워는 30년 간 관광객의 사랑을 받아 오다가 2005년 12월 9일 낙후된 시설을 리모델링해 산뜻하고 화려한 모습으로 탈바꿈했다. 또한, 장애인차량 전용도로가 있어 전망대까지 진입하기가 수월하다. N 서울타워는 남산의 새롭다(new)의 뜻을 지닌 알파벳 N을 합성한 이름으로 계절, 이벤트 내용에 따라 서울타워 외관의 색상과 패턴이 변화하는 조명시스템을 구축하였으며 매일 밤 7시부터 12시까지 서울의 꽃이라는 주제로 6개의 서치라이트가 다양한 각도로 하늘에 발사되어 꽃이 활짝 핀 모양을 연출한다. 원기둥 형태의 5층으로 된 타워룸은 2~3층 전망대와 5층에 자리잡은 회전전망 레스토랑이 압권을 이룬다. 회전전망레스토랑은 이름 그대로 회전식 레스토랑으로서 각각 160석을 갖추고 있다. 사방이 열려있는 원형식당은 48분 주기로 360도 회전해, 창가에 앉아 담소를 나누며 앉은 자리에서 자연스럽게 서울의 사방을 조망할 수 있다. 북악과 북한산은 물론 개성의 송악산이 눈에 들어오고, 한강과 남한산성, 관악산이 강남의 아파트촌 너머로 파노라마처럼 이어진다. 햇살이 기우는 저녁시간 서해로 기우는 저녁노을이 서강과 한강하구까지 물들여질 때를 가장 절경으로 꼽는다.','서울특별시 용산구 남산공원길 105 (용산동2가)','http://www.nseoultower.com','37.5511736','126.9860379');
--행 4
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('한국민속촌','서울/경기','용인시','한국민속촌은 서울에서 남쪽으로 약 41㎞, 경부고속도로 수원 I.C에서 동남쪽으로 3㎞ 가량 떨어진 곳에 있다. 민속촌은 민족문화자원을 보존하고, 자라나는 2세들의 교육을 위한 학습장 및 내외국인을 위한 전통문화의 소개 등을 설립 취지로 하여 1973년 착공하였으며 1974년에 완공되었다. 민속촌의 구성은 조선시대 후기의 한 시기를 택하여 당시의 생활상을 재현한 것이다. 특히 당대의 사농공상의 계층별 문화와 무속신앙, 세시풍속 등을 재현 전시하였다. 또 지방별로 특색을 갖춘 농가, 민가, 관가, 관아, 서원, 한약방, 서당, 대장간, 누정, 저자거리 등을 비롯하여 99칸 양반주택 등 대토호가도 재현되어 있고, 유기공방 등 전통공방을 생동감있게 재현하고 있다. 공연행사로는 농악, 마상무예, 전통혼례식 공연 및 기타 특별한 이벤트 행사가 절기별로 행하여지고 있다. 그리고 야외에서 재현.전시하기 어려운 부분은 옥내민속전시관을 통해 민속생활 전반을 재현 전시해 놓았다. 놀이시설로는 눈썰매장을 비롯하여 국내 유일의 화산폭발을 재현하고 있는 대규모 가족공원이 있다. 장터에 이르면 증편, 인절미, 북어구이, 빈대떡, 파전같은 우리 고유의 떡과 안주류에 순 재래식으로 담근 동동주를 맛볼 수 있다.','경기도 용인시 기흥구 민속촌로 90 (보라동)','http://www.koreanfolk.co.kr','37.2574502','127.1160245');
--행 5
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('설악산','강원','속초시','설악동 소공원 안의 깎아지른듯한 돌산 800m 위 80칸의 넓은 돌바닥 둘레에 쌓은 2,100m의 산성이 권금성이다. 산성을 만든 연대는 확실한 기록이 없어 알기 어렵다. 다만, 신라시대에 권씨와 김씨 두 장사가 난을 피하기 위해 쌓았다 하여 권금성이라고 한다. 한 마을에 살던 권씨와 김씨는 난을 당하여 가족들을 데리고 피난길에 오른다. 급한 나머지 산꼭대기로 올라갔으나, 성이 없어서 적병과 싸우기에는 너무 어려웠다. 권씨는 냇가의 돌로 성을 쌓자고 제안했고, 산 밑으로 내려가 돌을 던지는 것이었다. 이를 김씨가 받아 성을 만들기 시작하자 하룻밤 사이에 성의 모습이 윤곽을 갖췄다는 전설이 깃들어 있다. 권·김 두 장사가 쌓은 성이라고 해서 그 이름이 권금성이 되었다고 한다. 이 곳에 오르면 외설악의 절경과 동해의 끝없는 바다가 펼쳐져 보인다.','강원도 속초시 설악산로 1091 (설악동)','http://seorak.knps.or.kr/','38.1195498','128.4655581');
--행 6
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('대관령','강원','강릉시','2018년 평창동계올림픽이 열리는 평창군을 포함하는 대관령 관광특구는 강원도의 산악 지대와 해안을 아우르는 5개의 시군을 포함한다. 고원 지대의 빼어난 자연 경관을 지닌 대관령 관광특구는 강원도의 대표 관광 도시인 강릉시와 동해시, 삼척시, 횡성군, 평창군에 걸쳐 넓게 분포되어 있어 즐길 거리로 가득 차 있다. 강릉시는 대관령과 가장 인접한 지역으로 경포호와 경포대를 낀 한국의 대표적인 해양 관광지이자 오죽헌과 선교장을 중심으로 전통 문화가 살아 숨 쉬는 전통 문화 관광지이다. 영동고속도로와 연결되어 있어 서울에서 접근도 편리하다. 동해시에는 활기찬 항구의 풍경을 만날 수 있는 동해항과 묵호항이 있다. 동해 제1의 국제 무역항인 동해항에서는 러시아와 일본으로 가는 국제 여객선이 운항 중이다. 울릉도로 가는 배가 출항하는 묵호항은 묵호등대와 북평 5일장과 함께 동해시 관광을 대표하는 장소다.  삼척시는 강원도 최남단에 위치하며 국도 7호선을 중심으로 위로는 동해시, 아래로는 울진군과 접한 해양 휴양 관광지다. 국내 최대의 석회암 동굴인 환선굴과 대금굴이 있어 동굴체험 여행지로도 제격이다.  횡성군은 질 좋은 한우 생산지로 유명하며 다양한 농촌 체험을 할 수 있는 고즈넉한 시골 풍경을 자랑한다. 여유로운 휴식을 누리는 숲과 태기산의 절경을 감상하는 드라이브코스도 좋다. 해발 700m의 고원에 자리한 평창군은 국내 최대 규모를 자랑하는 리조트들이 있어 겨울 스포츠의 메카로 불린다.','강원도 강릉시, 동해시, 삼척시, 횡성군, 평창군 일원','http://www.gntour.go.kr/Tours','37.6871362','128.7605707');
--행 7
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('태백산','강원','태백시','민족의 영산이라 일컫는 태백산은 주봉인 높이 1,567m의 장군봉과 높이 1,517m의 문수봉으로 이루어진 산으로 높으되 가파르거나 험하지 않아 등산이 수월하며 남성다운 웅장함과 후덕함을 지닌 토산산이다. 산 정상에는 고산 식물이 자생하고 봄이면 산철쭉, 진달래가 만개하고 여름에는 울창한 수목에 차고 깨끗한 계곡물이 흐르며 가을에는 오색단풍으로 수놓으며 겨울에는 흰눈으로 뒤덮인 주목군락의 설경을 보여주는 곳이다. 태백산은 우리나라 3신산 중의 하나로 산 정상에는 태고 때부터 하늘에 제사를 지내던 천제단이 있고 이 곳에서의 일출 장면이 장관이다. 또한 당골 계곡에는 매년 개천절에 제를 올리는 단군성전이 있다. 사찰로는 망경사, 백단사, 유일사, 만덕사, 청원사 등이 있으며, 산 정상밑 해발 1,500m에는 단종대왕을 모신 단종 비각과 한국명수 100선중 으뜸인 용정이라는 우물이 있다. 또한 태백산도립공원 주변에는 한강의 발원지인 검룡소와 낙동강 발원지인 황지연못이 있으며, 태백석탄박물관과 구문소 자연 학습장으로 연계되는 코스는 화석, 지질 구조, 석탄산업의 발달사 등 학생들에게 유익한 현장 학습이 될 수 있다. ','강원도 태백시 천제단길 168 (소도동) 태백산도립공원사업소','http://tbmt.taebaek.go.kr','37.0957387','128.9152404');
--행 8
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('속리산 법주사','충북','보은군','속리산에 있는 법주사는 1400년전 신라에 불교가 들어온 지 24년째인 진흥왕 14년(서기 553년)에 의신조사가 창건됐다. 이후 776년 진표 및 영심 스님 대에 중창을 하였으며, 주선조 중기에 이르러서는 60여 동의 건물과 70여 개의 암자를 거느린 대찰로서 위용을 떨치게 되었다. 하지만 임진왜란으로 인해 사찰의 거의 모든 건물이 전소되었지만 1624년 인조 2년에 이르러 벽암 스님에 의해 또다시 중창을 하게 되었다. 경내에는 우리나라 3대 불상전 가운데 하나인 대웅보전을 중심으로 용화전, 원통보전, 명부전, 능인전, 조사각, 진영각, 삼성각 등 8개의 전각과 일주문, 금강문, 사천왕문, 종고루, 부도전 등 5개의 부속건축물, 그리고 선원, 강원, 염불원 등 3개의 원과 또한 염화당, 용화당, 미룡당, 응주전, 사리각, 종무소를 포함한 10여 채의 요사채들이 남아 있어 도합 30여동의 건물이 조성되어 있다고 말할 수 있다. ','충청북도 보은군 속리산면 법주사로 405 (속리산면)','http://www.beopjusa.or.kr, http://beopjusa.templestay.com','36.5423788','127.8336837');
--행 9
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('서천 국립생태원','충남','서천군','국립생태원은 자연환경의 보전과 국민의 행복추구권간의 지속 가능한 균형을 모색하기 위해 설립된 국가기관이다. 에코리움에는 열대, 온대, 지중해, 극지 등 다양한 생태계를 체험할 수 있으며, 습지생태계를 비롯한 야외생태공간에서는 다양한 생태체험과 교육이 진행된다.','충청남도 서천군 마서면 금강로 1210 ','http://www.nie.re.kr','36.0276334','126.7200186');
--행 10
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('공주 무령왕릉','충남','공주시','공주시 금성동과 웅진동에 연접한 옛 지명은 공주읍 송산리 宋山의 남쪽 경사면에 자리하고 있는 웅진시대(475~538) 백제의 왕과 왕족들의 무덤이다. 원래 17기의 무덤이 있었지만 현재는 무령왕릉을 포함하여 1~6호분까지 7기만 복원되어 있다. 이들 무덤은 만들어진 형태에 따라 굴식돌방무덤과 벽돌무덤의 두 종류로 구분된다. 1~5호분은 백제가 전통적으로 사용한 굴식돌방무덤으로, 돌로 널방을 만든 후 천장을 돔(Dome) 형태로 둥글게 처리하였으며, 6호분과 무령왕릉은 중국 남조의 영향을 받은 벽돌무덤으로 터널형 널방 앞에 짧은 터널형 널길을 가지고 있다. 무령왕릉을 제외한 나머지 무덤들은 도굴로 인하여 부장품이 거의 남아 있지 않았지만 웅진시대 백제 문화의 우수성과 활발했던 대외교류를 확인할 수 있는 대단히 중요한 유적이다. ','충청남도 공주시 왕릉로 37-2 (웅진동)','http://tour.gongju.go.kr, 문화재청 http://www.cha.go.kr','36.462094','127.1138786');
--행 11
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('서산 해미읍성','충남','서산시','조선 시대의 대표적인 읍성이다. 읍성이란 읍을 둘러싸고 세운 평지성으로 해미읍성 외에 고창읍성, 낙안읍성 등이 유명하다. 해미읍성은 조선 성종 22년, 1491년에 완성한 석성이다. 둘레는 약 1.8km, 높이 5m, 총면적 198,348m²(6만여 평)의 거대한 성으로 동,남,서의 세 문루가 있다. 최근 복원 및 정화사업을 벌여 옛 모습을 되찾아 사적공원으로 조성되었으며, 조선말 천주교도들의 순교 성지로도 유명하다. 천주교 박해 당시 관아가 있던 해미읍성으로 충청도 각 지역에서 수많은 신자가 잡혀와 고문받고 죽음을 당했으며, 특히, 1866년 박해때에는 1천여 명이 이 곳에서 처형됐다고 한다. 성내 광장에는 대원군 집정 당시 체포된 천주교도들이 갇혀 있던 감옥터와 나뭇가지에 매달려 모진 고문을 당했던 노거수 회화나무가 서 있다. 바로 성문밖 도로변에는 회화나무에 매달려 고문을 받으면서도 굴하지 않은 신도들을 돌 위에 태질해 살해했던 자리개돌이 있어 천주교도들의 순례지가 되고 있다. 성벽 주위에는 탱자나무를 심어 적병을 막는데 이용하였다고 하나, 지금은 거의 찾아볼 수가 없다.','충청남도 서산시 해미면 남문2로 143 (해미면)','http://www.haemifest.com, 문화재청 http://www.cha.go.kr ','36.7125761','126.5486189');
--행 12
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('순천만 자연 생태공원','전남','순천시','순천시 교량동과 대대동, 해룡면의 중흥리, 해창리 선학리 등에 걸쳐 있는 순천만 갈대밭의 총 면적은 약 15만평에 달한다. 순천 시내를 관통하는 동천과 순천시 상사면에서 흘러 온 이사천의 합수 지점부터 하구에 이르는 3㎞ 쯤의 물길양쪽이 죄다 갈대밭으로 뒤덮혀 있다. 그것도 드문드문 떨어져 있거나 성기게 군락을 이룬 여느 갈대밭과는 달리, 사람의 키보다 훨씬 더 웃자란 갈대들이 빈틈없이 밀생(密生)한 갈대밭이다. 갈대 군락지로는 국내 최대 규모라고 하는데, 갈대의 북슬북슬한 씨앗 뭉치가 햇살의 기운에 따라 은빛 잿빛 금빛 등으로 채색되는 모습이 아주 장관이다. 게다가 때마침 불어온 갯바람에 갈대숲 전체가 일제히 흐느적거리는 풍경은 망망한 바다에 일렁이는 물결처럼 장엄하고 아름답다.','전라남도 순천시 순천만길 513-25 (대대동)','http://www.suncheonbay.go.kr/ ','34.9275964','127.4643475');
--행 13
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('담양 죽녹원','전남','담양군','죽녹원은 관방제림과 영산강의 시원인 담양천을 끼고 있는 향교를 지나면 바로 왼편에 보이는 대숲이다. 약 16만㎡의 울창한 대숲으로 담양군이 성인산 일대를 조성하여 2003년 5월 개원했다. 죽녹원 입구에서 돌계단을 하나씩 하나씩 밟고 오르며 굳어있던 몸을 풀리고 대나무 사이로 불어오는 대바람이 일상에 지쳐있는 심신에 청량감을 불어 넣어준다. 또한 댓잎의 사각거리는 소리를 들으며 빽빽히 들어서 있는 대나무 숲길을 걷노라면 푸른 댓잎을 통과해 쏟아지는 햇살의 기운을 온 몸으로 받아내는 기분 또한 신선하다. 죽녹원 안에는 대나무 잎에서 떨어지는 이슬을 먹고 자란다는 죽로차가 자생하고 있다. 죽림욕을 즐기고 난 후 죽로차 한 잔으로 마음의 여유까지 느껴볼 수 있는 곳이다.','전라남도 담양군 담양읍 죽녹원로 119 (담양읍)','http://juknokwon.go.kr','35.3243649','126.9852444');
--행 14
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('보성 녹차밭','전남','보성군','전남 보성에 있는 대한다업 (주)보성다원을 관광농원으로 개방한 곳으로 1957년에 시작해 반세기를 내다보는 내력있는 차 관광농원이다. 대한다업에서는 1959년 해발 350m 보성 오선봉 주변에 대단위의 녹차밭을 조성하고 있으며, 현재 연간 녹차 120톤 이상을 생산하고 있다. 대한다업(주)는 봉산리에 있는 보성다원 제 1다원과 회천리에 있는 제 2다원이 있으며, 제1다원은 국내 유일 차 관광농원으로 지정운영되고 있다. 연간 다녀가는 관광객수도 100만명이 넘고 있으며, 각종 CF촬영과, 영화촬영지로도 유명하며, 영화 "선물"의 촬영 장소였던 울창한 삼나무숲 오솔길로 걸어오르는 차밭은 991,740m²(30여만 평) 규모의 대단위 차농원으로 장관을 이뤄놓고 있다. 2003년 7월에는 KBS드라마 여름향기가 촬영되기도 하였다.','전라남도 보성군 보성읍 녹차로 763-43 (보성읍)','http://www.dhdawon.com','34.7153791','127.0799346');
--행 15
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('여수 오동도','전남','여수시','여수하면 오동도, 오동도하면 동백꽃이 연상될 정도로 동백꽃이 유명한 섬이다. 또한, 한려해상국립공원의 기점이자 종점이기도 하다. 여수 중심가에서 승용차로 10여 분만 가면 닿는 오동도 입구의 주차장에 차를 세워두고 다시 768m 길이의 방파제 길을 15여분 걸으면 오동도에 도착한다. 면적 125,620.4m²(38,000평)의 섬 내에는 동백나무, 시누대 등 200여 종의 가종 상록수가 하늘을 가릴 정도로 울창하다. 또한, 16,529m²(5,000여 평)의 잔디광장 안에는 70여 종의 야생화가 심어진 화단과 기념식수동산 등이 있어 어린이들의 자연학습장으로도 유용하다. 섬 전체를 덮고 있는 3,000여 그루 동백나무는 이르면 10월부터 한두 송이씩 꽃이 피기 시작하기 때문에 한겨울에도 붉은 꽃을 볼 수 있다. 그리고 2월 중순경에는 약 30% 정도 개화되다가 3월 중순경에 절정을 이룬다. 섬 전체에 거미줄처럼 뻗어있는 탐방로는 연인들의 데이트 코스로 인기가 높고, 종합상가 횟집에서는 인근 남해 바다에서 갓잡아 올린 싱싱한 생선을 맛볼 수 있다.','전남 여수시 오동도로 242 ','http://www.odongdo.go.kr','34.7446826','127.7577429');
--행 16
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('전주한옥마을','전북','전주시','전주한옥마을은 전주시 풍남동과 교동에 위치해 있다. 이곳에는 한국의 전통 건물인 한옥이 800여 채나 밀집되어 있다. 빠르게 변화는 도시 속에 옛 것을 그대로 간직한 전주한옥마을은 한국의 옛 전통을 맛볼 수 있는 곳으로 유명하다. 전주한옥마을의 멋은 무엇보다도 한옥의 아름다운 지붕선에 있다. 지붕자락이 살짝 하늘로 향해 있는 것이 한옥의 특징이고 멋이다. 한옥의 구조는 크게 안채와 사랑채로 나뉘어져 진다. 안채는 여인들이 머무는 곳으로 규수방이 있고, 사랑채는 남자들이 사용하던 곳으로 선비방으로 되어 있다. 한옥은 남녀의 활동이 구분되어 있어 각각의 특징들을 가지고 있다. 그 중 안채의 경우는 여인들이 생활하는 곳이기 때문에 가장 은밀하고 깊숙한 곳에 위치해 있다. 한옥의 또 하나의 특징은 온돌방이다. 한국은 주로 앉아서 생활하기 때문에 바닥에 온돌을 깐다. 온돌의 구조는 방 밖에 아궁이(함실)을 만들고 구들(아궁이 안쪽)밑으로 불을 때면 온돌이 따뜻하게 데워지는 것이다. 여름에는 시원하고 겨울에는 따뜻하다는 것이 온돌의 가장 큰 특징이다. 이곳에 가면 한옥생활체험관이 따로 마련되어 있다. 한옥생활체험관안에 있는 선비방과 규수방에서 직접 온돌방 체험을 할 수도 있다. 한옥생활체험 중 이곳에서 제공되는 전통한식은 납청유기에 담아 맛과 멋을 더한다. 전주한옥마을에서 직접생활도 해보고 맛있기로 유명한 전주비빔밥도 맛보는 두가지 체험을 할 수 있는 곳이다.','전라북도 전주시 완산구 어진길 29 일대','http://hanok.jeonju.go.kr','35.8138476','127.14775');
--행 17
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('임실 치즈마을','전북','임실군','전북 임실 치즈마을은 한국 치즈의 원조 임실치즈의 뿌리를 가진 마을이다. 사람이 꽃보다 아름다운 치즈마을이란 테마로 더불어 사는 사회를 꿈꾸며 바른 먹을거리와 아이들의 미래를 먼저 생각하는 사람들이 모여 만든 마들로서 마을 사람들이 직접 진행하는 치즈낙농체험과 흥겨운 농촌체험을 통해 도시와 농촌이 하나되는 도농교류 경험을 할 수 있다.','전라북도 임실군 임실읍 치즈마을1길 4 ','http://cheese.invil.org','35.6362504','127.2926361');
--행 18
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('불국사/석굴암','경북','경주시','『구름을 마시고 토한다』는 토함산(745m)의 중턱에 자리하고 있는 불국사와 석굴암은 찬란한 신라 불교문화의 핵심으로 1,440년전 신라 법흥왕 22년에 그 어머니 뜻에 따라 나라의 안정과 백성의 평안을 위하여 세워졌으며, 그후 신라 경덕왕 (742-764)때 재상 김대성이 다시 지어 절의 면모을 새롭게 하였다. 국보 제24호인 석굴암은 신라 경덕왕 10년에 당시의 재상인 김대성에 의해 창건되었다고 전해진다. 불국사에서 산등성이를 타고 등산로로 3km, 신도로로는 9km거리에 있는 석굴암의 구조는 방형과 원형, 직선과 곡선,평면과 구면이 조화를 이루며 특히 벽 주위에 조각된 38체는 어느 것 하나 걸작이 아닌 것이 없고 전체적인 조화를 통해 고도의 철학성과 과학적인 면모를 나타내고 있으며 중앙에는 백색 화강암으로 된 여래좌상의 본존불이 동해를 굽어보고 있다.',' 경상북도 경주시 불국로 385 (진현동)','http://www.bulguksa.or.kr, http://www.bulguksa.org','35.7901014','129.3299037');
--행 19
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('안동 하회마을','경북','안동시','안동 하회 마을은 더이상 설명이 필요 없을 정도로 유명한 민속 마을이다. 낙동강이 마을을 휘감아 돌아가며 물돌이 마을을 이루고 있는 하회마을은 풍산 류씨의 집성촌으로 지금도 마을주민의 70%가 류씨이다. 풍산 류씨가 이곳에 자리를 잡은 것은 약 600여 년 전으로 류씨가 터를 잡기 전에는 허씨와 안씨가 먼저 이곳에 들어와 살고 있었다고 한다. 구전으로는 하회탈의 제작자가 고려 중엽의 허도령이라는 말이 전해지고 있다고 하며, 하회마을에서 강 건너 광덕동에는 허정승의 묘가 있어 해마다 류씨 가문에서 벌초를 하고 있다고 한다. 하회마을에는 이를 빗댄 "허씨 터전에, 안씨 문전, 류씨 배판"이라는 속언이 전해지고 있는데, 선주민들을 내몰게된데는 안씨집안의 딸 하나가 류씨집안으로 출가를 하면서 안씨 집안의 기운이 외손인 류씨들한테 이어져 씨족의 터전을 빼았기게 된 동기가 되었다고 하는 이야기이다.','경상북도 안동시 풍천면 하회종가길 40 ','http://www.hahoe.or.kr','36.5389541','128.2379901');
--행 20
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('부산 감천문화마을','경남','부산광역시','감천 문화 마을은 산기슭을 따라 밀집한 슬라브의 작은 집과 좁은 골목으로 이루어진 저소득층 밀집 지역이다. 태극도 마을은 2009년 예술 창작 단체인 ‘아트팩토리인다대포’ 주도로 문화체육관광부의 ‘공공 미술 프로젝트’[마을 곳곳에 조형물 10 점 설치]에 참여하면서 전국적으로 알려지게 된다. 현재는 마을 곳곳에 공공 미술 프로젝트로 인해 미관 개선 사업이 이루어졌으며, 문화체육관광부의 ‘2010 콘텐츠 융합형 관광 협력 사업’에 선정돼 문화 예술촌이 조성되고 있다. 마을의 빈집을 예술 창작실 혹은 갤러리로 개조하거나 북카페, 식당, 민박집 등으로 만들고, 마을 공터와 옥상을 생태 정원으로 바꾸는 등 주민 생활환경 개선 사업이 추진되고 있다. 현재 이 지역에는 감정초등학교, 감천 2동 주민자치센터, 감천2치안센터, 사하구 종합 복지 회관, 태극도총본부, 남성사, 천덕사, 감천 2동 체육공원 등이 있다.','부산 사하구 감내2로 177-11','http://www.gamcheon.or.kr/','35.0973948','129.0084037');
--행 21
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('해운대 해수욕장','경남','부산광역시','부산의 대표 해수욕장인 해운대해수욕장. 백사장의 길이 1.5km, 너비 30~50m, 평균수심 1m, 면적 58,400㎥의 규모로 넓은 백사장과 아름다운 해안선을 자랑하고 있으며 얕은 수심과 잔잔한 물결로 해수욕장의 최적 조건을 갖추고 있다. ‘부산’ 하면 제일 먼저 떠올리는 곳이 해운대 해수욕장이라고 할 만큼 부산을 대표하는 명소이며, 해마다 여름철 피서객을 가늠하는 척도로 이용될 만큼 국내 최대 인파가 몰리는 곳이기도 하다. 특히, 해안선 주변에 크고 작은 빌딩들과 고급 호텔들이 우뚝 솟아있어 현대적이고 세련된 분위기의 해수욕장으로 유명하기 때문에 여름 휴가철뿐만 아니라 사시사철 젊은 열기로 붐비고 해외 관광객들에게도 잘 알려져 있어 외국인들이 많이 찾는 곳이다.','부산광역시 해운대구 해운대해변로 264 (우동)','http://sunnfun.haeundae.go.kr ','35.1587142','129.1516294');
--행 22
INSERT INTO SIGHTS (SIGHTS_NAME, CITY, SIGHTS_REGION, SIGHTS_DESCRIPTION, SIGHTS_LOCATION, SIGHTS_HOMEPAGE, SIGHTS_LATITUDE, SIGHTS_LONGITUDE) VALUES ('지리산','경남','산청군','지리산은 금강산, 한라산과 더불어 삼신산의 하나로 알려져 왔으며, 신라 5악중 남악으로 어리석은 사람이 머물면 지혜로운 사람으로 달라진다 해서 지리산이라 불리워 왔다. 지리산은 백두산의 맥이 반도를 타고 내려와 이 곳까지 이어졌다는 뜻에서 두류산이라고 불리워지기도 하고, 불가에서 깨달음을 얻은 높은 스님의 처소를 가리키는 방장의 그 깊은 의미를 빌어 방장산이라고도 하였다. 지리산국립공원은 1967년 12월 29일 우리나라 최초의 국립공원으로 지정된 곳으로 경상남도 하동군, 산청군, 함양군, 전라남도 구례군, 전라북도 남원시 등 3개 도, 5개 시.군, 15개 읍.면에 걸쳐 있는 곳으로 그 면적이 440.517㎢에 이르고 있으며, 이를 환산하면 무려 1억 3천평이 넘는 면적이 된다. 이는 계룡산국립공원의 7배이고 여의도 면적의 52배 정도로 20개 국립공원 가운데서 육지면적만으로는 가장 넓다.',' 경남 산청군 시천면 , 삼장면','http://jiri.knps.or.kr ','35.3369611','127.7218007');

commit